import React from 'react'
import { observer } from 'mobx-react'
import _ from 'lodash'

class InvalidReport extends React.Component {
  constructor (props) {
    super(props)
    this.store = props.store
  }

  addClass (type) {
    let divClass = ''

    if (type === '' || type === undefined) {
      divClass = ''
    } else if (type === 'Success') {
      divClass = 'validReport'
    } else {
      divClass = 'invalidReport'
    }
    return divClass
  }

  addTypeClass (type) {
    let divClass = ''

    if (type === '' || type === undefined) {
      divClass = ''
    } else {
      divClass = 'validationTypeText'
    }

    return divClass
  }

  addTypeText (type) {
    let text = ''

    if (type === '' || type === undefined) {
      text = ''
    } else if (type === 'Success') {
      text = 'XMSG("Success")!'
    } else {
      let typeNoUnder = type.replace(/_/g, ' ')
      text = _.startCase(typeNoUnder)
    }

    return text
  }

  addDescText (type, desc) {
    let text = ''

    if (type === '' || type === undefined) {
      text = ''
    } else if (type === 'Success') {
      text = 'XMSG("Valid report description.")'
    } else {
      text = desc + '.'
    }

    return text
  }

  addImage (type) {
    let img = ''

    if (type === '' || type === undefined) {
      img = ''
    } else if (type === 'Success') {
      img = 'data:image/png;base64,'
    } else {
      img = 'data:image/png;base64,'
    }

    return img
  }

  addImgClass (type) {
    let divClass = ''

    if (type === '' || type === undefined) {
      divClass = ''
    } else {
      divClass = 'validationImgStyle'
    }

    return divClass
  }

  render () {
    const errorType = this.store.reportValidation.error_type
    const errorDesc = this.store.reportValidation.error_description
    const divClass = this.addClass(errorType)
    const typeClass = this.addTypeClass(errorType)
    const typeText = this.addTypeText(errorType)
    const descText = this.addDescText(errorType, errorDesc)
    const img = this.addImage(errorType)
    const imgClass = this.addImgClass(errorType)
    const imgDivStyle = {
      width: "20px",
      height: "auto"
    }
    const textStyle = {
      "marginLeft": "10px"
    }

    return (
      <div className={divClass}>
        <div style={imgDivStyle}>
          <img src={img} className={imgClass}/>
        </div>
        <div style={textStyle}>
          <div className={typeClass}>
            {typeText}
          </div>
          <div>
            {descText}
          </div>
        </div>
      </div>
    )
  }
}
export default observer(InvalidReport)
